import socket

from log import logger_zh, logger_en

matchmaking_config = bytes.fromhex("")

def server_check_hook():
    # assert if user has configured hosts file
    ip = socket.gethostbyname('gconnect.ubi.com')
    if ip != '127.0.0.1':
        logger_zh.info('Hosts文件检查失败！请将以下内容添加到hosts文件再重新运行本脚本：')
        logger_en.info('Hosts file check failed! You must add this line to hosts file before running this script:')
        print('127.0.0.1 gconnect.ubi.com')
        logger_zh.info('\n如果认为检查有误，可以忽略该错误。\n')
        logger_en.info('\nYou may ignore if you think I\'m wrong.\n')

    logger_zh.info('准备拦截服务器连接请求')
    logger_en.info('Ready to intercept server connection requests')
    sockserv = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
    sockserv.bind(('0.0.0.0', 3074))
    sockserv.listen(10)
    while True:
        (sock, a) = sockserv.accept()
        logger_zh.info('已拦截服务器连接请求')
        logger_en.info('MatchMakingConfig intercepted')
        print(f"[{sock.getpeername()}], [{sock.recv(1024)}]\n")
        sock.sendall(matchmaking_config)
        sock.close()

if __name__ == "__main__":
    server_check_hook()